import React, { useEffect, useState } from "react"
import "./App.css";
import PropTypes from "prop-types";
import ListBooks from "./ListBooks";
import * as BooksAPI from "./BooksAPI";

const SearchBooks = ({ updateBookShelf }) => {

    const [query, setQuery] = useState("");
    const [showingBooks, setShowingBooks] = useState([]);

    const updateQuery = (event) => {
      const { value } = event.target;
      setQuery(value);
    };

    // const searchBooks = async (query) => {
    //   if (query === '') {
    //     setShowingBooks([])
    //   } else {
    //    // const res = await BooksAPI.search(query, 20);
    //     const allBooks = await BooksAPI.getAll()
    //     let filteredBooks = allBooks.filter((b) => b?.title?.toLowerCase().includes(query.toLowerCase()) || b?.author?.toLowerCase().includes(query.toLowerCase()))
    //     // let searchedBooks = [];
    //     // let k = 0;
        
    //     // for (let i = 0; i < allBooks.length; i++) {
    //     //   for (let j = 0; j < res.length; j++) {
    //     //     if (allBooks?.[i]?.id === res?.[j]?.id) {
    //     //       searchedBooks[k] = allBooks[i]
    //     //       k++;
    //     //     }
    //     //   }
    //     // }

    //     setShowingBooks(filteredBooks)

    //   filteredBooks.forEach(e => console.log(e.title +' ' +e.shelf))

    // }};
    

    useEffect(() => {
      const searchBooks = async (query) => {
        if (query === '') {
          setShowingBooks([])
        } else {
         // const res = await BooksAPI.search(query, 20);
          const allBooks = await BooksAPI.getAll()
          let filteredBooks = allBooks.filter((b) => b?.title?.toLowerCase().includes(query.toLowerCase()) || b?.author?.toLowerCase().includes(query.toLowerCase()))
          // let searchedBooks = [];
          // let k = 0;
          
          // for (let i = 0; i < allBooks.length; i++) {
          //   for (let j = 0; j < res.length; j++) {
          //     if (allBooks?.[i]?.id === res?.[j]?.id) {
          //       searchedBooks[k] = allBooks[i]
          //       k++;
          //     }
          //   }
          // }
  
          setShowingBooks(filteredBooks)
  
        filteredBooks.forEach(e => console.log(e.title +' ' +e.shelf))
  
      }};
        searchBooks(query);
    }, [query]);


  return (
    
    <div className="search-books">
    <div className="search-books-bar">
      <a
        className="close-search"
        href="/"
      >
        Close
      </a>
      <div className="search-books-input-wrapper">
        <input
          type="text"
          placeholder="Search by title or author"
          value={query}
          onChange={e=> updateQuery(e)}
        />
      </div>
    </div>
    <div className="search-books-results">
    </div>
    {showingBooks?.length > 0 && (<ListBooks books={showingBooks} updateBookShelf={updateBookShelf}></ListBooks>)}
  </div>
  );
};

SearchBooks.propTypes = {
    updateBookShelf: PropTypes.func,
  };

export default SearchBooks;
