import "./App.css";
import React, { useState, useEffect } from "react";
import ShelvedBooks from "./ShelvedBooks"
import * as BooksAPI from "./BooksAPI";
import SearchBooks from "./SearchBooks";
import { Route, Routes, useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";
import ListBooks from "./ListBooks";

function App() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    const getBooks = async () => {
      const res = await BooksAPI.getAll();
      setBooks(res);
      console.log(res)
    };
    getBooks();
  }, []);

  const updateBookShelf = (book, shelf) => {
    const update = async () => {
      const res = await BooksAPI.update(book, shelf);
      setBooks(res);
  }; 
  update();
}

const [query, setQuery] = useState("");

    const updateQuery = (event) => {
      const { value } = event.target;
      setQuery(value);
      console.log(showingBooks.length)
    };
  
  
    const clearQuery = () => {
      updateQuery("");
    };

    const showingBooks=
    query === ""
      ? books
      : books.filter((c) =>
          c.title.toLowerCase().includes(query.toLowerCase())
        );

  return (
    <div className="app">
    <Routes>
      <Route
        exact
        path="/"
        element={
          <div className="list-books">
    <div className="list-books-title">
      <h1>MyReads</h1>
    </div>
    <Link to="/search">
          Add Contact
        </Link>
    <div className="list-books-content">
      <div>
        <div className="bookshelf">
          <h2 className="bookshelf-title">Currently Reading</h2>
          <div className="bookshelf-books">
            <ListBooks books={books} filter="currentlyReading" updateBookShelf={updateBookShelf}></ListBooks>
        </div>
        </div>
        <div className="bookshelf">
          <h2 className="bookshelf-title">Want to Read</h2>
          <div className="bookshelf-books">
            <ListBooks books={books} filter="wantToRead" updateBookShelf={updateBookShelf}></ListBooks>
          </div>
        </div>
        <div className="bookshelf">
          <h2 className="bookshelf-title">Read</h2>
          <div className="bookshelf-books">
            <ListBooks books={books} filter="read" updateBookShelf={updateBookShelf}></ListBooks>
          </div>
        </div>
      </div>
    </div>
  </div>
        }
      />
      <Route
        path="/search"
        element={
          <div className="search-books">
    <div className="search-books-bar">
      {/* <a
        className="close-search"
        onClick
      >
        Close
      </a> */}
      <div className="search-books-input-wrapper">
        <input
          type="text"
          placeholder="Search by title, author, or ISBN"
          value={query}
          onChange={updateQuery}
        />
      </div>
    </div>
    <div className="search-books-results">
    <ListBooks books={books}></ListBooks>
      {showingBooks.length > 0 && <ListBooks books={showingBooks}></ListBooks>}
    </div>
  </div>
        }
      />
    </Routes>
    </div>
  );
}

export default App;
